import { Component } from '@angular/core';

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css']
})
export class NavBarComponent {
  isNavbarCollapsed = true;

  constructor() { }

  ngOnInit(): void {
  }

  toggleNavbar(): void {
    this.isNavbarCollapsed = !this.isNavbarCollapsed;
  }


}
<footer class="bg-dark text-light">
  <div class="container py-3">
    <div class="row">
      <div class="col-md-6">
        <p>&copy; 2023 Allan Bascur</p>
      </div>
      <div class="col-md-6 text-md-right">
        <ul class="list-inline">
          <li class="list-inline-item"><a href="#inicio">Inicio</a></li>
          <li class="list-inline-item"><a href="#about">Sobre Mí</a></li>
          <li class="list-inline-item"><a href="#skills">Habilidades</a></li>
          <li class="list-inline-item"><a href="#projects">Proyectos</a></li>
          <li class="list-inline-item"><a href="#contact">Contacto</a></li>
        </ul>
      </div>
    </div>
  </div>
  
</footer>
