import { Component } from '@angular/core';

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css']
})
export class NavBarComponent {
  isNavbarCollapsed = true;

  constructor() { }

  ngOnInit(): void {
  }

  toggleNavbar(): void {
    this.isNavbarCollapsed = !this.isNavbarCollapsed;
  }


}
<nav class="navbar">
  <div class="navbar-brand">
    <a href="#">Logo</a>
  </div>
  <ul class="navbar-nav">
    <li class="nav-item">
      <a href="#">Inicio</a>
    </li>
    <li class="nav-item">
      <a href="#">Blog</a>
    </li>
    <li class="nav-item">
      <a href="#">Proyectos</a>
    </li>
    <li class="nav-item">
      <a href="#">Contacto</a>
    </li>
  </ul>
</nav>
